import{_ as e,o as a,c as r,a as h}from"./app.984096d0.js";const i={},d=h('<h1 id="\u8FD9\u4E00\u5207\u90FD\u4E0D\u662F\u6211\u4EEC\u7684\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#\u8FD9\u4E00\u5207\u90FD\u4E0D\u662F\u6211\u4EEC\u7684\u9009\u62E9" aria-hidden="true">#</a> \u8FD9\u4E00\u5207\u90FD\u4E0D\u662F\u6211\u4EEC\u7684\u9009\u62E9</h1><blockquote><p>\u6211\u4EEC\u767B\u4E0A\u5E76\u975E\u6211\u4EEC\u6240\u9009\u62E9\u7684\u821E\u53F0\uFF0C\u6F14\u51FA\u5E76\u975E\u6211\u4EEC\u6240\u9009\u62E9\u7684\u5267\u672C\u3002</p></blockquote><p>2020\u5E74\uFF0C\u4E5F\u8BB8\u662F\u8FD1\u4E9B\u5E74\u6700\u7279\u6B8A\u7684\u4E00\u5E74\u4E86\u3002\u5E2D\u5377\u5168\u7403\u7684\u75AB\u60C5\u3001\u4E1C\u975E\u8757\u707E\u3001\u75AF\u72C2\u7684\u7F8E\u80A1\u7194\u65AD\u3001\u539F\u6CB9\u4E8B\u4EF6... \u9664\u4E86\u8FD9\u4E9B\u56FD\u9645\u4E0A\u7684\u5927\u4E8B\u4EF6\uFF0C\u5BF9\u4E8E\u4E2A\u4EBA\u6765\u8BF4\uFF0C\u4E5F\u53EF\u4EE5\u79F0\u5F97\u4E0A\u662F\u5173\u952E\u7684\u4E00\u5E74\uFF0C\u6BD5\u4E1A\u524D\u7684\u6700\u540E\u4E00\u5E74\uFF0C\u5373\u5C06\u5B8C\u6210\u8EAB\u4EFD\u7684\u8F6C\u6362\u3002</p><p>\u8FD9\u4E9B\u90FD\u4E0D\u662F\u6211\u4EEC\u6240\u9009\u62E9\u76842020\uFF0C\u800C\u662F\u8FD9\u4E9B\u4E8B\u4EF6\u6784\u6210\u4E862020\u3002\u8FD9\u4E00\u5207\u90FD\u4E0D\u662F\u6211\u4EEC\u7684\u9009\u62E9\uFF0C\u6240\u4EE5\u6211\u4E00\u70B9\u4E5F\u4E0D\u6000\u5FF5\u5B83\u3002</p><h1 id="\u6211\u76842020" tabindex="-1"><a class="header-anchor" href="#\u6211\u76842020" aria-hidden="true">#</a> \u6211\u76842020</h1><p>\u5C3D\u7BA1\u60F3\u4E86\u5F88\u4E45\u600E\u4E48\u5199\u4E00\u7BC72020\u5E74\u7684\u603B\u7ED3\uFF0C\u4F46\u662F\u6BCF\u6B21\u5199\u4E86\u4E00\u4E9B\u4E1C\u897F\u90FD\u4F1A\u88AB\u6211\u5220\u53BB\u3002\u56E0\u4E3A\u5B9E\u5728\u662F\u6CA1\u6709\u529E\u6CD5\u5C06\u81EA\u5DF1\u7684\u60F3\u6CD5\u5B8C\u6574\u7684\u8868\u8FF0\u51FA\u6765\uFF0C\u6240\u4EE5\u6211\u6253\u7B97\u5199\u4E00\u70B9\u7B97\u4E0D\u4E0A\u603B\u7ED3\u7684\u4E1C\u897F\u3002</p><blockquote><p>TODO\uFF0C\u7B49\u6211\u6709\u65F6\u95F4\u6211\u8981\u628A\u4EE5\u524D\u5199\u7684\u603B\u7ED3\u4E5F\u8D34\u51FA\u6765\u3002</p></blockquote><h2 id="\u8BFB\u4E66" tabindex="-1"><a class="header-anchor" href="#\u8BFB\u4E66" aria-hidden="true">#</a> \u8BFB\u4E66</h2><p>\u4ECA\u5E74\u8BFB\u4E66\u76F8\u5BF9\u4E8E\u53BB\u5E74\u6765\u8BF4\u662F\u5C11\u4E86\u4E00\u4E9B\u3002\u548C\u5927\u591A\u6570\u7A0B\u5E8F\u5458\u4E00\u6837\uFF0C\u6211\u770B\u7684\u4E66\u5927\u90E8\u5206\u4E5F\u662F\u6280\u672F\u4E66\u7C4D\uFF0C\u7531\u4E8E\u81EA\u8EAB\u662F\u4E2A\u5F69\u7B14\uFF0C\u5BF9\u4E8E\u6280\u672F\u4E66\u7C4D\u6CA1\u5565\u597D\u8BC4\u4EF7\u7684\uFF0C\u603B\u80FD\u6216\u591A\u6216\u5C11\u7684\u5B66\u5230\u4E00\u70B9\u4E1C\u897F\uFF08\u56E0\u4E3A\u770B\u4E0D\u4E0B\u53BB\u5C31\u5F03\u4E86\uFF09\u3002\u4ECA\u5E74\u57FA\u672C\u628A\u53BB\u5E74618\u4E70\u7684\u4E66\u5168\u90E8\u770B\u5B8C\u4E86\uFF0C\u5C3D\u7BA1\u6211\u4E2A\u4EBA\u5BF9\u4E8E\u5C0F\u8BF4\u66F4\u611F\u5174\u8DA3\uFF0C\u4F46\u662F\u4ECA\u5E74\u8BA9\u6211\u66F4\u52A0\u5370\u8C61\u6DF1\u523B\u7684\u662F\u4E24\u672C\u975E\u5C0F\u8BF4\u4E66\u7C4D\uFF0C\u4E00\u672C\u662F\u300A\u4EBA\u7C7B\u7B80\u53F2\u300B\uFF0C\u53E6\u4E00\u672C\u662F\u300A\u6FC0\u8361\u7684\u4E09\u5341\u5E74\u300B\u3002\u8FD9\u4E24\u672C\u4E66\u6709\u4E00\u4E2A\u5171\u540C\u7684\u7279\u70B9\uFF0C\u8BED\u8A00\u90FD\u975E\u5E38\u7B80\u5355\u6613\u61C2\u3002\u5C24\u5176\u662F\u5BF9\u4E8E\u7B2C\u4E00\u672C\uFF0C\u63D0\u51FA\u4E86\u5F88\u591A\u6709\u610F\u601D\u7684\u89C2\u70B9\uFF0C\u201C\u56FD\u5BB6\uFF0C\u5236\u5EA6\uFF0C\u8D22\u5BCC\u7B49\u90FD\u662F\u5EFA\u7ACB\u5728\u4EBA\u4EEC\u76F8\u4FE1\u7684\u6545\u4E8B\u4E0A\u201D\u3002\u6211\u6E05\u695A\u7684\u8BB0\u7740\u521A\u770B\u5B8C\u8FD9\u672C\u4E66\u7684\u65F6\u5019\u60F3\u5199\u4E00\u4E2A\u8BFB\u540E\u611F\u6765\u7740\uFF0C\u540E\u9762\u5FD9\u4E8E\u627E\u5B9E\u4E60\u5C31\u6401\u7F6E\u4E86\u3002\u5BF9\u4E8E\u7B2C\u4E8C\u672C\u4E66\uFF0C\u5219\u8BB2\u7684\u662F\u4E2D\u56FD\u8FD1\u73B0\u4EE3\u5404\u4E2A\u5927\u4F6C\u7684\u53D1\u5BB6\u81F4\u5BCC\u7684\u5C0F\u6545\u4E8B\u3002\u5C3D\u7BA1\u6BCF\u4E00\u4E2A\u90FD\u5F88\u7B80\u7565\uFF0C\u4F46\u662F\u8FD8\u662F\u80FD\u770B\u51FA\u65F6\u4EE3\u7684\u80CC\u5F71\uFF0C\u6BD5\u7ADF\u201C\u4E00\u4E2A\u4EBA\u7684\u547D\u8FD0\uFF0C\u5F53\u7136\u8981\u9760\u81EA\u6211\u594B\u6597\uFF0C\u4F46\u4E5F\u8981\u8003\u8651\u5230\u5386\u53F2\u7684\u8FDB\u7A0B\u201D\u3002</p><p>\u6211\u5BF9\u7AF9\u54E5\u8BF4\u8FC7\uFF0C\u80FD\u5728\u4E66\u4E0A\u627E\u5230\u8BA4\u540C\u7684\u611F\u89C9\u771F\u597D\uFF0C\u627E\u5230\u67D0\u4E9B\u60C5\u611F\u5171\u9E23\u5B9E\u5728\u662F\u8BFB\u4E66\u7684\u4E00\u5927\u4E50\u4E8B\u3002\u800C\u86C7\u795E\u5219\u51C6\u786E\u7684\u8868\u8FF0\u4E3A\uFF1A\u53EF\u4EE5\u4ECE\u4E66\u91CC\u627E\u5230\u81EA\u5DF1\u5185\u5FC3\u8BA4\u53EF\u53EA\u662F\u4E0D\u80FD\u7CBE\u786E\u8868\u8FBE\u7684\u9053\u7406[1]\u3002\u4EE5\u540E\u6211\u5E94\u8BE5\u591A\u770B\u4E00\u4E9B\u975E\u6280\u672F\u4E66\u7C4D\uFF0C<strong>\u6B64\u5916\u5BF9\u4E8E\u65B9\u6CD5\u8BBA\u7684\u4E66\uFF0C\u4E5F\u8BB8\u6211\u4E0D\u8BE5\u8FD9\u4E48\u6392\u65A5\u5B83\uFF0C\u4E07\u4E00\u5B83\u771F\u7684\u6709\u7528\u5462\uFF1F</strong></p><h2 id="\u5A31\u4E50" tabindex="-1"><a class="header-anchor" href="#\u5A31\u4E50" aria-hidden="true">#</a> \u5A31\u4E50</h2><p>\u5A31\u4E50\u771F\u662F\u4E00\u79CD\u5929\u8D4B\u6280\u80FD\u3002\u6211\u53D1\u73B0\u6211\u8FD1\u4E24\u5E74\u5BF9\u4E8E\u5FEB\u4E50\u7684\u611F\u77E5\u80FD\u529B\u6025\u901F\u7684\u4E0B\u964D\uFF0C\u800C\u4E14\u5FEB\u4E50\u7EF4\u6301\u7684\u65F6\u95F4\u6781\u5176\u77ED\u6682\uFF0C\u5F80\u5F80\u5728\u70ED\u95F9\u4E4B\u540E\u56DE\u53BB\u7684\u8DEF\u4E0A\u7A81\u7136\u4F24\u611F\u8D77\u6765\uFF0C\u5DF2\u7136\u5FEB\u53D8\u6210\u7965\u6797\u5AC2\u4E86\u3002\u65E0\u8BBA\u505A\u4EC0\u4E48\u4E8B\u60C5\uFF0C\u5F88\u5BB9\u6613\u5C31\u611F\u5230\u4E4F\u5473\uFF0C\u7F3A\u4E4F\u5FC5\u8981\u7684\u6FC0\u60C5\u3002\u65E5\u5E38\u89C9\u5F97\u65E0\u4E8B\u53EF\u505A\uFF0C\u65E5\u5E38\u89C9\u5F97\u7410\u4E8B\u4E0D\u60F3\u505A\uFF0C\u65E5\u5E38\u89C9\u5F97\u4E8B\u60C5\u6CA1\u6709\u610F\u4E49\u3002</p><p>\u611F\u53D7\u4E8B\u60C5\u7684\u672C\u8D28\uFF0C\u4E5F\u8BB8\u662F\u8BA9\u4E00\u4E2A\u4EBA\u5FC3\u60C5\u6109\u60A6\u7684\u597D\u529E\u6CD5\u3002\u5728\u67D0\u622A\u56FE\u4E2D\u770B\u5230\u848B\u65B9\u821F\u63D0\u5230\u8981\u8BA4\u771F\u5480\u56BC\u5927\u7C73\u7684\u5473\u9053\uFF0C\u8FD9\u6837\u4F1A\u8BA9\u751F\u6D3B\u8FC7\u5F97\u66F4\u5F00\u5FC3\u3002\u8FD9\u5E94\u8BE5\u7B97\u4F5C\u662F\u8BA4\u771F\u7684\u5BF9\u5F85\u98DF\u7269\u672C\u8EAB\u3002\u6211\u65F6\u5E38\u4F1A\u611F\u6168\uFF0C\u201C\u4E3A\u5565LGQ\u603B\u662F\u89C9\u5F97\u8FD9\u4E1C\u897F\u8FD9\u4E48\u597D\u5403\uFF1F\u4E3A\u5565LHS\u80FD\u89C9\u5F97\u8FD9\u4E2A\u6E38\u620F\u8FD9\u4E48\u597D\u73A9\uFF1F\u201D\u3002\u6C89\u6D78\u5230\u4E00\u4E2A\u4E8B\u60C5\u4E2D\uFF0C\u65E0\u8BBA\u662F\u5403\u996D\u3001\u6E38\u620F\uFF0C\u90FD\u80FD\u5F97\u5230\u5F88\u5927\u7684\u5FEB\u4E50\u3002\u8FD9\u70B9\u6211\u4F3C\u4E4E\u8981\u597D\u597D\u5B66\u4E60\u4E00\u4E0B\u3002</p><p>\u8BF4\u6765\u4ECA\u5E74\u6700\u5927\u7684\u53D8\u5316\u662F\uFF0C\u5728\u6708\u5E95\u7684\u65F6\u5019\uFF0C\u6211\u7684\u6BCF\u5929\u5165\u7761\u524D\u542C\u5F97\u4E0D\u518D\u662F\u97F3\u4E50\u4E86\uFF0C\u6211\u5F00\u59CB\u4E60\u60EF\u4E8E\u542C\u522B\u4EBA\u7684\u64AD\u5BA2\u3002\u542C\u522B\u4EBA\u5206\u4EAB\u5404\u79CD\u89C2\u70B9\u3001\u6709\u8DA3\u7684\u4E8B\u60C5\u3002\u5BF9\u4E8E\u7A0B\u5E8F\u5458\u6765\u8BF4\uFF0C\u64AD\u5BA2\u771F\u7684\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u6D88\u9063\u9014\u5F84\uFF0C\u4E00\u6574\u5929\u90FD\u662F\u5BF9\u7740\u7535\u8111\uFF0C\u73A9\u624B\u673A\u5237\u89C6\u9891\u5E76\u7B97\u4E0D\u5F97\u662F\u771F\u6B63\u7684\u4F11\u606F\uFF0C\u800C\u95ED\u4E0A\u773C\u775B\u4EC0\u4E48\u90FD\u4E0D\u60F3\u53C8\u4E0D\u662F\u5F88\u73B0\u5B9E\uFF0C\u56E0\u4E3A\u64AD\u5BA2\u8FD9\u79CD\u4FE1\u606F\u5BC6\u5EA6\u4E0D\u662F\u975E\u5E38\u9AD8\u7684\u5BF9\u8BDD\u65B9\u5F0F\u5C31\u975E\u5E38\u9002\u5408\u4F5C\u4E3A\u4E00\u79CD\u4F11\u95F2\u5A31\u4E50\u65B9\u5F0F\uFF0C\u53EA\u8981\u4F60\u628A\u5B83\u5F53\u6210\u53E6\u4E00\u79CD\u5F62\u5F0F\u7684\u6D4F\u89C8\u5E16\u5B50\u6216\u89C6\u9891\u3002</p><blockquote><p>\u5E0C\u671B\u80FD\u6709\u4EBA\u63A8\u8350\u4E00\u4E2A\u597D\u7684\u64AD\u5BA2\u9891\u9053\uFF0C\u9898\u6750\u4E0D\u9650\u3002\uFF08\u4F46\u662F\u5E76\u4E0D\u4F1A\u6709\u4EBA\u770B\u5230\u8FD9\u4E2A\u6CA1\u6709\u4EFB\u4F55\u4FE1\u606F\u7684\u5E74\u7EC8\u603B\u7ED3\uFF09</p></blockquote><h2 id="\u79D1\u7814" tabindex="-1"><a class="header-anchor" href="#\u79D1\u7814" aria-hidden="true">#</a> \u79D1\u7814</h2><p>\u4ECA\u5E74\u79D1\u7814\u7B97\u662F\u6BD4\u8F83\u987A\u5229\u7684\u4E00\u5E74\uFF0C\u5728\u5E08\u5144\u7684\u5168\u529B\u8F85\u52A9\u4E0B\u53D1\u8868\u4E86\u4E00\u7BC7EMNLP\uFF0C\u8FD9\u4E5F\u8BB8\u662F\u4ECA\u5E74\u6700\u5F00\u5FC3\u7684\u4E00\u4EF6\u4E8B\u4E86\u3002\u6211\u8BFB\u7814\u7684\u65F6\u5019\u76EE\u6807\u5C31\u662F\u5E0C\u671B\u80FD\u53D1\u8868\u4E00\u7BC7NLP\u9886\u57DF\u91CC\u9762\u7684\u9876\u4F1A\uFF0C\u8FD9\u7B97\u662F\u8FBE\u5230\u4E86\u4E4B\u524D\u7684\u76EE\u6807\uFF0C\u662F\u4E00\u79CD\u5C11\u6709\u7684\u5B8C\u6210\u76EE\u6807\u7684\u6210\u5C31\u611F\u3002\u5728\u8FD9\u91CC\uFF0C\u6211\u53EA\u60F3\u611F\u6069\uFF0C\u5BA2\u89C2\u4E0A\u6211\u7855\u58EB\u671F\u95F4\u8FD0\u6C14\u771F\u7684\u5F88\u597D\u3002\u6709\u7684\u65F6\u5019\u5FC3\u7406\u4E0A\u89C9\u5F97\u4E0D\u6109\u5FEB\u4E5F\u53EA\u662F\u56E0\u4E3A\u4EBA\u603B\u662F\u4F1A\u653E\u5927\u60B2\u4F24\u7684\u60C5\u7EEA\u3002</p><p>\u5BF9\u4E8E\u8BBA\u6587\u8FD9\u4EF6\u4E8B\uFF0C\u7ED9\u6211\u5E26\u6765\u7684\u6700\u5927\u7684\u6536\u76CA\u662F\u5FC3\u91CC\u6210\u5C31\u611F\u3002\u4E5F\u8BB8\u672C\u8D28\u4E0A\u6211\u662F\u60F3\u505A\u4E00\u4E9B\u5177\u6709\u6210\u5C31\u611F\u7684\u4E1C\u897F\uFF0C\u800C\u76EE\u524D\u6765\u8BF4\uFF0C\u6211\u6240\u80FD\u63A5\u89E6\u5230\u7684\u3001\u76F8\u5BF9\u6BD4\u8F83\u7B80\u5355\u7684\uFF0C\u4E5F\u5C31\u53EA\u6709\u201C\u79D1\u7814\u201D\uFF0C\u81F3\u5C11\u6211\u662F\u8BA4\u53EF\u5B83\u7684\u3002\u5728\u6211\u770B\u6765\uFF0C\u80FD\u5728\u8BBA\u6587\u4E2D\u7559\u4E0B\u540D\u5B57\uFF0C\u662F\u4E00\u4EF6\u975E\u5E38\u503C\u5F97\u5F00\u5FC3\u7684\u4E8B\u60C5\u3002\u56E0\u4E3A\u4E07\u4E00\uFF0C\u6709\u4E00\u4E2A\u79D1\u7814\u5DE5\u4F5C\u8005\uFF0C\u56E0\u4E3A\u4F60\u8FD9\u8FB9\u704C\u6C34\u7684\u6587\u7AE0\u6536\u5230\u4E00\u4E1D\u7684\u542F\u53D1\uFF0C\u800C\u540E\u53C8\u6709\u540E\u5E8F\u7684\u5DE5\u4F5C\u5728\u6B64\u4E0A\u9762\u8FDB\u884C\u6539\u8FDB\uFF0C\u4E07\u4E00\u8981\u662F\u641E\u51FA\u4E2A\u5927\u65B0\u95FB\uFF0C\u90A3\u4E5F\u7B97\u662F\u4E3A\u793E\u4F1A\u521B\u9020\u4E86\u4E00\u4E1D\u7684\u4EF7\u503C\u3002\u6BD5\u7ADF\u201C\u8774\u8776\u6548\u5E94\u201D\u662F\u4E0D\u53EF\u9884\u6D4B\u7684\u3002</p><h2 id="\u5DE5\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C" aria-hidden="true">#</a> \u5DE5\u4F5C</h2><p>\u5BF9\u4E8E\u5DE5\u4F5C\u6765\u8BF4\uFF0C\u4ECA\u5E74\u53BB\u4E86\u5B9E\u4E60\uFF0C\u4E5F\u6700\u7EC8\u5728\u79CB\u62DB\u627E\u5230\u4E86\u4E00\u4EFD\u5DE5\u4F5C\u3002\u5C3D\u7BA1\u5728\u5916\u4EBA\u770B\u6765\u6211\u62FF\u7684offer\u8FD8\u662F\u4E0D\u9519\u7684\uFF0C\u4F46\u662F\u6211\u4E2A\u4EBA\u5BF9\u6B64\u786E\u5B9E\u8FDC\u8FDC\u79F0\u4E0D\u4E0A\u6EE1\u610F\uFF08\u6211\u60F3\u7684\u662F\u8981\u6211\u662FXxx\u8BA1\u5212\u5C31\u597D\u4E86\uFF0C\u60F3\u5C41\u5403\uFF09\u3002\u6211\u5373\u5C06\u4E5F\u8981\u5165\u804C\u4E86\uFF0C\u5E0C\u671B\u5DE5\u4F5C\u4E86\u80FD\u7ED9\u6211\u5E26\u6765\u4E00\u4E9B\u65B0\u7684\u4E1C\u897F\uFF0C\u65E0\u8BBA\u662F\u77E5\u8BC6\u5C42\u9762\u8FD8\u662F\u6280\u80FD\u5C42\u9762\uFF0C\u8FD9\u4E5F\u662F\u6211\u5BF9\u4E8E\u5DE5\u4F5C\u6240\u62B1\u6709\u7684\u671F\u5F85\u3002\u5C3D\u7BA1\u6709\u5728\u516C\u53F8\u5B9E\u4E60\u8FC7\uFF0C\u4F46\u662F\u5DE5\u4F5C\u662F\u548C\u5B9E\u4E60\u5168\u7136\u4E0D\u540C\u7684\u5FC3\u6001\uFF0C\u5728\u5DE5\u4F5C\u7684\u65F6\u5019\uFF0C\u6211\u6240\u9700\u8981\u601D\u8003\u7684\u5C31\u662F\u5982\u4F55\u5E72\u6D3B\u81EA\u5DF1\u7684\u672C\u804C\u5DE5\u4F5C\uFF0C\u800C\u5BF9\u4E8E\u5B9E\u4E60\u5219\u603B\u662F\u4F1A\u6709\u4E00\u4E9B\u7410\u4E8B\u6240\u5E72\u6270\uFF0C\u4E5F\u8BB8\u5E76\u4E0D\u80FD\u5168\u8EAB\u5FC3\u6295\u5165\u5DE5\u4F5C\u4E2D\u3002\u5728\u5176\u4ED6\u65B9\u9762\uFF0C\u6211\u89C9\u5F97\u5DE5\u4F5C\u53EF\u80FD\u6CA1\u6709\u4EC0\u4E48\u597D\u8BF4\u7684\uFF0C\u56E0\u4E3A\u8FD9\u5DE5\u4F5C\u8FD9\u4E8B\u60C5\u603B\u4F1A\u6162\u6162\u53D8\u597D\u5427\uFF1F</p><h2 id="\u6539\u53D8" tabindex="-1"><a class="header-anchor" href="#\u6539\u53D8" aria-hidden="true">#</a> \u6539\u53D8</h2><p>\u6700\u8FD1\u6211\u6C89\u6EBA\u4E8E\u770B\u5404\u79CD\u5927\u4F6C\u5199\u7684\u5E74\u7EC8\u603B\u7ED3\uFF0C\u5BC4\u5E0C\u671B\u4E8E\u4ECE\u522B\u4EBA\u7684\u6210\u957F\u7ECF\u5386\u4E2D\u7AA5\u5F97\u4E00\u4E1D\u7384\u673A\uFF0C\u8BA9\u81EA\u5DF1\u7684\u4EBA\u751F\u53EF\u4EE5\u5F97\u5230\u4E00\u4E9B\u6307\u5F15\u3002\u7ECF\u8FC7\u6211\u4E0D\u8D1F\u8D23\u4EFB\u7684\u603B\u7ED3\uFF0C\u6211\u89C9\u5F97\u6211\u9700\u8981\u505A\u7684\u6539\u53D8\u7684\u6709\u4E09\u4E2A\u70B9\uFF1A</p><ul><li>\u89C4\u5212</li><li>\u884C\u52A8\u529B</li><li>\u4E13\u6CE8\u5EA6</li></ul><p>\u6211\u53EF\u4EE5\u8BF4\u662F\u4E00\u4E2A\u5F88\u6CA1\u6709\u89C4\u5212\u7684\u4EBA\uFF0C\u65E0\u8BBA\u9762\u5BF9\u4EC0\u4E48\u4E8B\u60C5\uFF0C\u6211\u5F88\u5BB9\u6613\u56FF\u4E8E\u81EA\u5DF1\u7684\u60F3\u8C61\u4E4B\u4E2D\u3002\u7531\u4E8E\u60F3\u8C61\u662F\u4E00\u4E2A\u5F88\u4E0D\u5177\u4F53\u7684\u4E1C\u897F\uFF0C\u6240\u4EE5\u603B\u662F\u5BB9\u6613\u5938\u5927\u6216\u8005\u5F31\u5316\u5176\u4E2D\u7684\u9700\u8981\u4ED8\u51FA\u52AA\u529B\u3002\u524D\u4E9B\u65F6\u95F4\u548C\u9AD8\u535A\u804A\u5929\u7684\u65F6\u5019\uFF0C\u4ED6\u63D0\u51FA\u4E86\u4E00\u4E9B\u6211\u4EE5\u524D\u6CA1\u6709\u60F3\u8FC7\u7684\u4E1C\u897F\u3002\u6BD4\u5982\uFF0C\u5927\u5BB6\u90FD\u60F3\u8D5A\u94B1\uFF0C\u90A3\u4E48\u4F60\u7684\u76EE\u6807\u662F\u8D5A\u591A\u5C11\u94B1\uFF1F\u90A3\u4E48\u80AF\u5B9A\u4F1A\u6709\u4EBA\u8BF4\u8D8A\u591A\u8D8A\u597D\uFF0C\u4F46\u662F\u8FD9\u672C\u8D28\u4E0A\u662F\u5077\u61D2\u7684\u56DE\u7B54\uFF0C\u56E0\u4E3A\u5982\u679C\u6211\u4EEC\u6CA1\u6709\u4E00\u4E2A\u5F88\u660E\u786E\u7684\u76EE\u6807\uFF0C\u90A3\u4E48\u5C31\u6BD4\u8F83\u96BE\u957F\u65F6\u95F4\u7684\u4E3A\u6B64\u4ED8\u51FA\u52AA\u529B\u3002\u56E0\u6B64\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5B9A\u4E00\u4E2A\u975E\u5E38\u5177\u4F53\u5316\u7684\u76EE\u6807\uFF0C\u6BD4\u5982\u4ECA\u5E74\u5E0C\u671B\u6708\u85AA\u80FD\u8FBE\u523020K\u3002\u800C\u5BF9\u4E8E\u5176\u4ED6\u4E8B\u60C5\u6765\u8BF4\uFF0C\u4E5F\u5B58\u5728\u540C\u6837\u7684\u60F3\u8C61\u548C\u5B9E\u9645\u4E2D\u7684GAP\uFF0C\u6211\u4EEC\u5E94\u8BE5\u5B9A\u4E49\u4E00\u4E9B\u8FC7\u7A0B\u76EE\u6807\uFF0C\u4ECE\u800C\u4F7F\u5F97\u76EE\u6807\u6E05\u6670\u4E00\u70B9\u3002\u5F53\u7136\u8FD9\u53EA\u662F\u89C4\u5212\u7684\u4E00\u79CD\uFF0C\u6211\u4EEC\u53EF\u4EE5\u79F0\u4E4B\u4E3A\u76EE\u6807\u89C4\u5212\u3002\u9664\u4E86\u76EE\u6807\u89C4\u5212\u4E4B\u5916\uFF0C\u8FC7\u7A0B\u89C4\u5212\u540C\u6837\u975E\u5E38\u91CD\u8981\uFF0C\u80FD\u591F\u5408\u7406\u5730\u5B89\u6392\u81EA\u5DF1\u5728\u6BCF\u4E2A\u8FC7\u7A0B\u4E2D\u9700\u8981\u505A\u7684\u4E8B\u60C5\uFF0C\u5C06\u4EFB\u52A1\u3001\u76EE\u6807\u8FDB\u884C\u80A2\u89E3\u5C06\u80FD\u591F\u6709\u6548\u5730\u63D0\u5347\u5DE5\u4F5C\u6548\u7387\u3002</p><p>\u884C\u52A8\u529B\u662FLGQ\u4E00\u4E2A\u53E6\u5916\u4E00\u4E2A\u8BA9\u6211\u5F88\u4F69\u670D\u7684\u70B9\u3002\u4F3C\u4E4E\u6BCF\u4E00\u4E2A\u4EBA\u90FD\u559C\u6B22\u4E3A\u81EA\u5DF1\u4E0D\u60F3\u505A\u67D0\u4EF6\u4E8B\u627E\u501F\u53E3\u3002\u201C\u4ECA\u5929\u6211\u5934\u6655\uFF0C\u4E0D\u662F\u5F88\u60F3\u8BFB\u8BBA\u6587\u3002\u660E\u5929\u518D\u770B\u4E5F\u662F\u4E00\u6837\u7684\u3002\u201D\u800C\u4EFB\u4F55\u65F6\u5019\u6211\u4EEC\u90FD\u80FD\u627E\u5230\u4E00\u4E2A\u5408\u9002\u7684\u501F\u53E3\u63A8\u8131\uFF0C\u54EA\u6015\u5B9E\u9645\u4E0A\u5E76\u4E0D\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u501F\u53E3\uFF0C\u6211\u4EEC\u4E5F\u4F1A\u5F88\u5BB9\u6613\u5C31\u63A5\u53D7\u8FD9\u4E2A\u501F\u53E3\uFF0C\u56E0\u4E3A\u4E0D\u7BA1\u600E\u4E48\u8BF4\uFF0C\u5927\u90E8\u5206\u4EBA\u90FD\u662F\u4E2A\u61D2\u60F0\u7684\u4EBA\u5462\u3002\u201C\u79CD\u4E00\u68F5\u6811\u6700\u597D\u7684\u65F6\u5149\u662F\u5341\u5E74\u524D\uFF0C\u5176\u6B21\u662F\u73B0\u5728!\u201D \u56E0\u6B64\u63A5\u4E0B\u6765\uFF0C\u5BF9\u4E8E\u4E00\u4E9B\u65E0\u6CD5\u907F\u514D\u8981\u5904\u7406\u7684\u4E8B\u60C5\uFF0C\u6211\u5C06\u66F4\u5FEB\u901F\u7684\u884C\u52A8\u8D77\u6765\u3002\uFF08\u55EF\uFF0C\u5B9E\u9645\u4E0A\u6211\u5DF2\u7ECF\u5728\u62D6\u4E86\uFF0C\u6BD4\u5982\u8FD9\u7BC7\u5E9F\u8BDD\u603B\u7ED3\uFF09</p><p>\u6211\u65F6\u5E38\u5BF9\u81EA\u5DF1\u5FC3\u733F\u610F\u9A6C\u800C\u611F\u5230\u82E6\u607C\uFF0C\u56E0\u6B64\u4E5F\u66FE\u5C1D\u8BD5\u8FC7\u4E00\u4E9B\u5F31\u667A\u529E\u6CD5\uFF0C\u6BD4\u5982\u524D\u9762\u4E5F\u6709\u6587\u7AE0\u63D0\u5230\u8FC7\u7684\u957F\u65F6\u95F4\u5173\u95ED\u670B\u53CB\u5708\u5165\u53E3\uFF08\u8FD1\u671F\u56E0\u4E3A\u4E34\u8FD1\u6BD5\u4E1A\u6709\u4E86\u65B0\u7684\u60F3\u6CD5\u800C\u518D\u6B21\u6D3B\u8DC3\u8D77\u6765\uFF09\uFF0C\u4F46\u662F\u8FD9\u4E9B\u65B9\u5F0F\u90FD\u6539\u53D8\u4E0D\u4E86\u81EA\u5DF1\u4E0D\u591F\u4E13\u6CE8\u7684\u672C\u8D28\u3002\u4E13\u6CE8\u5EA6\u51B3\u5B9A\u4E86\u6211\u4EEC\u505A\u4E00\u4EF6\u4E8B\u60C5\u7684\u6548\u7387\uFF0C\u524D\u9762\u7684\u6587\u7AE0\u91CC\u9762\u5C31\u63D0\u5230\u8FC7\u5F88\u591A\u6B21\u5BF9\u4E8E\u81EA\u5DF1\u7684\u4E00\u4E9B\u4E0D\u6EE1\u6765\u81EA\u4E8E\u201C\u4E0D\u4E13\u6CE8\u201D\uFF0C\u800C\u81EA\u5DF1\u4E5F\u56E0\u6B64\u611F\u5230\u6781\u5927\u7684\u75DB\u82E6\u3002\uFF08\u5B9E\u9645\u4E0A\u5212\u6C34\u662F\u771F\u7684\u5F00\u5FC3\uFF0C\u4E8B\u540E\u624D\u662F\u75DB\u82E6\u7684\u3002\uFF09</p><p>\u4E3A\u4E86\u5E94\u5BF9\u8FD9\u4E09\u4E2A\u6539\u53D8\uFF0C\u6211\u5F00\u59CB\u5C1D\u8BD5\u4E86\u4F7F\u7528\u6E05\u5355\u548C\u756A\u8304\u949F\uFF0C\u5BF9\u6211\u6765\u8BF4\u7B97\u662F\u521D\u6709\u6210\u6548\uFF0C\u65E0\u8BBA\u662F\u5BF9\u4E8E\u884C\u52A8\u529B\u3001\u8FD8\u662F\u4E13\u6CE8\u529B\u76F8\u5BF9\u4E8E\u4E4B\u524D\u5F31\u9E21\u7684\u6211\uFF0C\u90FD\u6709\u4E86\u4E00\u5B9A\u7684\u63D0\u5347\u3002\u800C\u5728\u4F7F\u7528\u4EFB\u52A1\u6E05\u5355\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u5C31\u4F1A\u4E0D\u81EA\u89C9\u5730\u8FDB\u884C\u76EE\u6807\u7684\u89C4\u5212\u548C\u4EFB\u52A1\u7684\u80A2\u89E3\uFF0C\u56E0\u6B64\u5728\u4EFB\u52A1\u5B8C\u6210\u5EA6\u6709\u4E86\u660E\u663E\u7684\u63D0\u5347\u3002\u6B64\u5916\u6211\u5F00\u59CB\u5C1D\u8BD5\u4F7F\u7528\u4F7F\u7528\u756A\u8304\u949F\uFF0C\u81F3\u5C11\u5728\u4E00\u4E2A\u756A\u8304\u949F\u768425\u5206\u949F\u5185\uFF0C\u4E0D\u73A9\u624B\u673A\uFF0C\u53EA\u505A\u4E00\u4EF6\u4E8B\u60C5\u3002\u6BD5\u7ADF\u54EA\u6015\u53EA\u5B66\u4E6025\u5206\u949F\uFF0C\u4E5F\u7B97\u662F\u597D\u4E8B\u554A\u3002\u3002\u76EE\u524D\u201C\u89C4\u5212\u201D\u3001\u201C\u884C\u52A8\u529B\u201D\u3001\u201C\u4E13\u6CE8\u5EA6\u201D\u8FD9\u4E09\u4E2A\u70B9\u90FD\u6709\u4E86\u4E00\u5B9A\u7684\u63D0\u5347\uFF0C\u63A5\u4E0B\u6765\u9700\u8981\u8FDB\u884C\u7684\u5C31\u662F\u5F97\u5230\u8DB3\u591F\u7684\u6295\u5165\u3002\u201C\u79CD\u4E00\u68F5\u6811\u6700\u597D\u7684\u65F6\u5149\u662F\u5341\u5E74\u524D\u201D\u8BF4\u7684\u53E6\u5916\u4E00\u4EF6\u4E8B\u662F\u201C\u957F\u6210\u4E00\u68F5\u6811\u9700\u8981\u5F88\u957F\u7684\u65F6\u95F4\u201D\u3002\u5728\u6E05\u6670\u5408\u7406\u7684\u76EE\u6807\u63A8\u52A8\u4E0B\uFF0C\u6211\u4EEC\u7684\u6295\u5165\u90A3\u4E48\u5C31\u4F1A\u88AB\u8D4B\u4E88\u66F4\u52A0\u6DF1\u5165\u7684\u4E00\u5C42\u610F\u4E49\uFF0C\u8FD9\u5C31\u662F\u5E38\u8BF4\u7684\u8FC7\u7A0B\u8D22\u5BCC\u3002\uFF08\u5F53\u7136\u5927\u6982\u7387\u5176\u5B9E\u662F\u6C89\u6CA1\u6210\u672C\uFF09</p><h1 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h1><p>[1] https://www.yuque.com/xcodebuild/thinking/pogui1</p>',29),t=[d];function n(p,c){return a(),r("div",null,t)}const s=e(i,[["render",n],["__file","index.html.vue"]]);export{s as default};
